'use strict';

var fm = require('FileManager');
var path = require('path');
var fs = require('fs');

var thumbs = fm.getFilesArray('videos/_thumbs/', ['*.jpg']);

for (i in thumbs) {
  var newFile = path.join(path.dirname(thumbs[i]), path.basename(thumbs[i], '.webm.jpg') + "-thumb.jpg");
  fs.renameSync(thumbs[i], newFile);
}

